contracts/AlertRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title AlertRegistry
 * @notice Immutable on-chain log of all alerts Genesis has fired.
 *         Provides a tamper-proof audit trail — no one can delete or modify
 *         historical alerts.
 *
 *         KEY BLOCKCHAIN FEATURES:
 *           1. Immutable audit trail (alerts can never be edited/deleted)
 *           2. On-chain timestamping
 *           3. Alert verification (anyone can verify an alert was real)
 *           4. Decentralised alert history (not in a private database)
 */
contract AlertRegistry is Ownable {

    // ──────────────────── Types ─────────────────────
    enum Severity { LOW, MEDIUM, HIGH, CRITICAL }

    struct Alert {
        uint256 id;
        address triggeredBy;      // which address caused the alert
        address token;            // which token was involved
        uint256 amount;           // transaction amount
        Severity severity;
        string alertType;         // "whale_transfer", "rapid_outflow", etc.
        string summary;           // human-readable summary (from CyreneAI)
        uint256 blockNumber;
        uint256 timestamp;
    }

    // ──────────────────── Storage ───────────────────
    Alert[] public alerts;
    uint256 public alertCount;

    // Authorised recorders (Genesis backend address)
    mapping(address => bool) public authorisedRecorders;

    // ──────────────────── Events ────────────────────
    event AlertRecorded(
        uint256 indexed alertId,
        address indexed triggeredBy,
        address indexed token,
        uint256 amount,
        Severity severity,
        string alertType,
        string summary,
        uint256 blockNumber,
        uint256 timestamp
    );

    event RecorderAuthorised(address indexed recorder, uint256 timestamp);
    event RecorderRevoked(address indexed recorder, uint256 timestamp);

    // ──────────────────── Modifiers ─────────────────
    modifier onlyRecorder() {
        require(
            authorisedRecorders[msg.sender] || msg.sender == owner(),
            "AlertRegistry: not authorised"
        );
        _;
    }

    // ──────────────────── Constructor ────────────────
    constructor() Ownable(msg.sender) {
        // Owner is automatically an authorised recorder
        authorisedRecorders[msg.sender] = true;
    }

    // ──────────────────── Core ──────────────────────

    /**
     * @notice Record an alert on-chain. Called by Genesis backend.
     *         Once written, the alert is IMMUTABLE.
     */
    function recordAlert(
        address triggeredBy,
        address token,
        uint256 amount,
        Severity severity,
        string calldata alertType,
        string calldata summary
    ) external onlyRecorder returns (uint256 alertId) {
        alertId = alertCount++;

        alerts.push(Alert({
            id: alertId,
            triggeredBy: triggeredBy,
            token: token,
            amount: amount,
            severity: severity,
            alertType: alertType,
            summary: summary,
            blockNumber: block.number,
            timestamp: block.timestamp
        }));

        emit AlertRecorded(
            alertId,
            triggeredBy,
            token,
            amount,
            severity,
            alertType,
            summary,
            block.number,
            block.timestamp
        );
    }

    // ──────────────────── Admin ─────────────────────

    function authoriseRecorder(address recorder) external onlyOwner {
        authorisedRecorders[recorder] = true;
        emit RecorderAuthorised(recorder, block.timestamp);
    }

    function revokeRecorder(address recorder) external onlyOwner {
        authorisedRecorders[recorder] = false;
        emit RecorderRevoked(recorder, block.timestamp);
    }

    // ──────────────────── View ──────────────────────

    function getAlert(uint256 alertId) external view returns (Alert memory) {
        require(alertId < alertCount, "Invalid alert ID");
        return alerts[alertId];
    }

    function getRecentAlerts(uint256 count) external view returns (Alert[] memory) {
        uint256 start = alertCount > count ? alertCount - count : 0;
        uint256 length = alertCount - start;
        Alert[] memory recent = new Alert[](length);
        for (uint256 i = 0; i < length; i++) {
            recent[i] = alerts[start + i];
        }
        return recent;
    }

    function getAlertsBySeverity(Severity severity) external view returns (Alert[] memory) {
        // Count first
        uint256 count = 0;
        for (uint256 i = 0; i < alertCount; i++) {
            if (alerts[i].severity == severity) count++;
        }
        // Collect
        Alert[] memory result = new Alert[](count);
        uint256 idx = 0;
        for (uint256 i = 0; i < alertCount; i++) {
            if (alerts[i].severity == severity) {
                result[idx++] = alerts[i];
            }
        }
        return result;
    }
}

contracts/GenesisGovernance.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title GenesisGovernance
 * @notice On-chain governance: proposals, voting, execution.
 *         Genesis monitors all governance activity in real time.
 *
 *         KEY FEATURES:
 *           1. Anyone can create a proposal (with min token balance)
 *           2. Token holders vote For / Against / Abstain
 *           3. Voting period with configurable duration
 *           4. Proposal execution after passing
 *           5. Rich events for every action (Genesis monitors these)
 *
 *         SIMPLIFIED FOR HACKATHON:
 *           - Uses simple token balance snapshot (no delegation)
 *           - No timelock on execution
 *           - Quorum is configurable
 */
contract GenesisGovernance is Ownable {

    // ──────────────────── Types ─────────────────────
    enum ProposalState { Active, Passed, Failed, Executed, Cancelled }
    enum VoteType { Against, For, Abstain }

    struct Proposal {
        uint256 id;
        address proposer;
        string title;
        string description;
        uint256 votesFor;
        uint256 votesAgainst;
        uint256 votesAbstain;
        uint256 startTime;
        uint256 endTime;
        ProposalState state;
        bool executed;
    }

    // ──────────────────── Storage ───────────────────
    Proposal[] public proposals;
    
    // proposalId → voter → hasVoted
    mapping(uint256 => mapping(address => bool)) public hasVoted;
    
    // proposalId → voter → voteType
    mapping(uint256 => mapping(address => VoteType)) public voterChoice;

    // Config
    uint256 public votingDuration = 3 days;  // default
    uint256 public quorumVotes = 1000e6;     // minimum votes to pass (in token units)
    uint256 public proposalThreshold = 100e6; // min tokens to create proposal

    // Stats
    uint256 public totalProposals;
    uint256 public totalVotesCast;

    // ──────────────────── Events ────────────────────
    event ProposalCreated(
        uint256 indexed proposalId,
        address indexed proposer,
        string title,
        string description,
        uint256 startTime,
        uint256 endTime,
        uint256 timestamp
    );

    event VoteCast(
        uint256 indexed proposalId,
        address indexed voter,
        VoteType voteType,
        uint256 weight,
        string reason,
        uint256 timestamp
    );

    event ProposalStateChanged(
        uint256 indexed proposalId,
        ProposalState oldState,
        ProposalState newState,
        uint256 timestamp
    );

    event ProposalExecuted(
        uint256 indexed proposalId,
        address indexed executor,
        uint256 votesFor,
        uint256 votesAgainst,
        uint256 timestamp
    );

    event ProposalCancelled(
        uint256 indexed proposalId,
        address indexed canceller,
        string reason,
        uint256 timestamp
    );

    event GovernanceConfigChanged(
        string parameter,
        uint256 oldValue,
        uint256 newValue,
        uint256 timestamp
    );

    // ──────────────────── Constructor ────────────────
    constructor() Ownable(msg.sender) {}

    // ──────────────────── Proposal functions ─────────

    /**
     * @notice Create a new governance proposal.
     * @param title Short title
     * @param description Detailed description
     * @param durationOverride Custom voting duration (0 = use default)
     */
    function createProposal(
        string calldata title,
        string calldata description,
        uint256 durationOverride
    ) external returns (uint256 proposalId) {
        require(bytes(title).length > 0, "Gov: empty title");

        uint256 duration = durationOverride > 0 ? durationOverride : votingDuration;

        proposalId = proposals.length;
        proposals.push(Proposal({
            id: proposalId,
            proposer: msg.sender,
            title: title,
            description: description,
            votesFor: 0,
            votesAgainst: 0,
            votesAbstain: 0,
            startTime: block.timestamp,
            endTime: block.timestamp + duration,
            state: ProposalState.Active,
            executed: false
        }));

        totalProposals++;

        emit ProposalCreated(
            proposalId,
            msg.sender,
            title,
            description,
            block.timestamp,
            block.timestamp + duration,
            block.timestamp
        );
    }

    /**
     * @notice Cast a vote on an active proposal.
     * @param proposalId The proposal to vote on
     * @param voteType 0=Against, 1=For, 2=Abstain
     * @param weight Vote weight (in token units — caller self-reports for demo)
     * @param reason Optional reason string
     */
    function castVote(
        uint256 proposalId,
        VoteType voteType,
        uint256 weight,
        string calldata reason
    ) external {
        require(proposalId < proposals.length, "Gov: invalid proposal");
        Proposal storage p = proposals[proposalId];
        require(p.state == ProposalState.Active, "Gov: not active");
        require(block.timestamp <= p.endTime, "Gov: voting ended");
        require(!hasVoted[proposalId][msg.sender], "Gov: already voted");
        require(weight > 0, "Gov: zero weight");

        hasVoted[proposalId][msg.sender] = true;
        voterChoice[proposalId][msg.sender] = voteType;

        if (voteType == VoteType.For) {
            p.votesFor += weight;
        } else if (voteType == VoteType.Against) {
            p.votesAgainst += weight;
        } else {
            p.votesAbstain += weight;
        }

        totalVotesCast++;

        emit VoteCast(
            proposalId,
            msg.sender,
            voteType,
            weight,
            reason,
            block.timestamp
        );
    }

    /**
     * @notice Finalize a proposal after voting ends.
     * @param proposalId The proposal to finalize
     */
    function finalizeProposal(uint256 proposalId) external {
        require(proposalId < proposals.length, "Gov: invalid proposal");
        Proposal storage p = proposals[proposalId];
        require(p.state == ProposalState.Active, "Gov: not active");

        ProposalState oldState = p.state;
        uint256 totalVotes = p.votesFor + p.votesAgainst + p.votesAbstain;

        // Check quorum and majority
        if (totalVotes >= quorumVotes && p.votesFor > p.votesAgainst) {
            p.state = ProposalState.Passed;
        } else {
            p.state = ProposalState.Failed;
        }

        emit ProposalStateChanged(proposalId, oldState, p.state, block.timestamp);
    }

    /**
     * @notice Execute a passed proposal.
     * @param proposalId The proposal to execute
     */
    function executeProposal(uint256 proposalId) external {
        require(proposalId < proposals.length, "Gov: invalid proposal");
        Proposal storage p = proposals[proposalId];
        require(p.state == ProposalState.Passed, "Gov: not passed");
        require(!p.executed, "Gov: already executed");

        p.executed = true;
        p.state = ProposalState.Executed;

        emit ProposalExecuted(
            proposalId,
            msg.sender,
            p.votesFor,
            p.votesAgainst,
            block.timestamp
        );

        emit ProposalStateChanged(proposalId, ProposalState.Passed, ProposalState.Executed, block.timestamp);
    }

    /**
     * @notice Cancel a proposal (proposer or owner).
     * @param proposalId The proposal to cancel
     * @param reason Reason for cancellation
     */
    function cancelProposal(uint256 proposalId, string calldata reason) external {
        require(proposalId < proposals.length, "Gov: invalid proposal");
        Proposal storage p = proposals[proposalId];
        require(
            msg.sender == p.proposer || msg.sender == owner(),
            "Gov: not authorized"
        );
        require(p.state == ProposalState.Active, "Gov: not active");

        ProposalState oldState = p.state;
        p.state = ProposalState.Cancelled;

        emit ProposalCancelled(proposalId, msg.sender, reason, block.timestamp);
        emit ProposalStateChanged(proposalId, oldState, ProposalState.Cancelled, block.timestamp);
    }

    // ──────────────────── Config (owner only) ───────

    function setVotingDuration(uint256 newDuration) external onlyOwner {
        uint256 old = votingDuration;
        votingDuration = newDuration;
        emit GovernanceConfigChanged("votingDuration", old, newDuration, block.timestamp);
    }

    function setQuorum(uint256 newQuorum) external onlyOwner {
        uint256 old = quorumVotes;
        quorumVotes = newQuorum;
        emit GovernanceConfigChanged("quorumVotes", old, newQuorum, block.timestamp);
    }

    function setProposalThreshold(uint256 newThreshold) external onlyOwner {
        uint256 old = proposalThreshold;
        proposalThreshold = newThreshold;
        emit GovernanceConfigChanged("proposalThreshold", old, newThreshold, block.timestamp);
    }

    // ──────────────────── View functions ─────────────

    function getProposalCount() external view returns (uint256) {
        return proposals.length;
    }

    function getProposalInfo(uint256 proposalId) external view returns (
        address proposer,
        string memory title,
        uint256 votesFor,
        uint256 votesAgainst,
        uint256 votesAbstain,
        ProposalState state,
        bool executed
    ) {
        Proposal storage p = proposals[proposalId];
        return (p.proposer, p.title, p.votesFor, p.votesAgainst, p.votesAbstain, p.state, p.executed);
    }

    function getVoteResult(uint256 proposalId) external view returns (
        uint256 forVotes,
        uint256 againstVotes,
        uint256 abstainVotes,
        uint256 totalVotes,
        bool quorumReached,
        bool passed
    ) {
        Proposal storage p = proposals[proposalId];
        uint256 total = p.votesFor + p.votesAgainst + p.votesAbstain;
        return (
            p.votesFor,
            p.votesAgainst,
            p.votesAbstain,
            total,
            total >= quorumVotes,
            p.votesFor > p.votesAgainst && total >= quorumVotes
        );
    }
}

contracts/GenesisLiquidityPool.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title GenesisLiquidityPool
 * @notice Simplified AMM-style liquidity pool for hackathon demo.
 *         Monitors liquidity additions, removals, swaps, and rebalancing.
 *
 *         This simulates a DEX pool (like Uniswap) with two tokens:
 *           - Token A (e.g., gUSD — our stablecoin)
 *           - Token B (e.g., gETH — mock ETH for the pair)
 *
 *         KEY FEATURES:
 *           1. Add liquidity (deposit both tokens into pool)
 *           2. Remove liquidity (withdraw proportional share)
 *           3. Swap tokens (simplified constant-product formula)
 *           4. Pool stats (TVL, reserves, LP share tracking)
 *           5. Rich events for Genesis monitoring pipeline
 *
 *         SIMPLIFIED FOR HACKATHON:
 *           - No LP token minting (tracks shares internally)
 *           - Simplified swap fee (0.3%)
 *           - No flash loan protection (not needed for demo)
 */
contract GenesisLiquidityPool is Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    // ──────────────────── Storage ───────────────────
    IERC20 public tokenA;  // e.g., gUSD
    IERC20 public tokenB;  // e.g., gETH
    string public poolName;

    uint256 public reserveA;
    uint256 public reserveB;
    uint256 public totalLPShares;

    // user → LP shares
    mapping(address => uint256) public lpShares;

    // Config
    uint256 public swapFeeNumerator = 3;     // 0.3% fee
    uint256 public swapFeeDenominator = 1000;

    // Stats
    uint256 public totalSwaps;
    uint256 public totalFeesCollectedA;
    uint256 public totalFeesCollectedB;
    uint256 public totalLiquidityAdded;
    uint256 public totalLiquidityRemoved;

    // ──────────────────── Events ────────────────────
    event LiquidityAdded(
        address indexed provider,
        uint256 amountA,
        uint256 amountB,
        uint256 lpSharesMinted,
        uint256 newReserveA,
        uint256 newReserveB,
        uint256 timestamp
    );

    event LiquidityRemoved(
        address indexed provider,
        uint256 amountA,
        uint256 amountB,
        uint256 lpSharesBurned,
        uint256 newReserveA,
        uint256 newReserveB,
        uint256 timestamp
    );

    event Swap(
        address indexed trader,
        address tokenIn,
        address tokenOut,
        uint256 amountIn,
        uint256 amountOut,
        uint256 fee,
        uint256 newReserveA,
        uint256 newReserveB,
        uint256 timestamp
    );

    event PoolRebalanced(
        address indexed triggeredBy,
        uint256 oldReserveA,
        uint256 oldReserveB,
        uint256 newReserveA,
        uint256 newReserveB,
        string reason,
        uint256 timestamp
    );

    event LargeSwapDetected(
        address indexed trader,
        uint256 amountIn,
        uint256 amountOut,
        uint256 priceImpactBps, // basis points
        uint256 timestamp
    );

    // ──────────────────── Constructor ────────────────
    constructor(
        address _tokenA,
        address _tokenB,
        string memory _poolName
    ) Ownable(msg.sender) {
        require(_tokenA != address(0) && _tokenB != address(0), "Pool: zero addr");
        require(_tokenA != _tokenB, "Pool: same tokens");
        tokenA = IERC20(_tokenA);
        tokenB = IERC20(_tokenB);
        poolName = _poolName;
    }

    // ──────────────────── Core functions ─────────────

    /**
     * @notice Add liquidity to the pool. First deposit sets the ratio.
     * @param amountA Amount of token A to add
     * @param amountB Amount of token B to add
     */
    function addLiquidity(uint256 amountA, uint256 amountB) external nonReentrant returns (uint256 shares) {
        require(amountA > 0 && amountB > 0, "Pool: zero amounts");

        // Transfer tokens in
        tokenA.safeTransferFrom(msg.sender, address(this), amountA);
        tokenB.safeTransferFrom(msg.sender, address(this), amountB);

        // Calculate LP shares
        if (totalLPShares == 0) {
            // First deposit — shares = sqrt(amountA * amountB) simplified
            shares = _sqrt(amountA * amountB);
        } else {
            // Proportional to existing reserves
            uint256 shareA = (amountA * totalLPShares) / reserveA;
            uint256 shareB = (amountB * totalLPShares) / reserveB;
            shares = shareA < shareB ? shareA : shareB; // min
        }

        require(shares > 0, "Pool: zero shares");

        lpShares[msg.sender] += shares;
        totalLPShares += shares;
        reserveA += amountA;
        reserveB += amountB;
        totalLiquidityAdded += amountA + amountB;

        emit LiquidityAdded(
            msg.sender,
            amountA,
            amountB,
            shares,
            reserveA,
            reserveB,
            block.timestamp
        );
    }

    /**
     * @notice Remove liquidity from the pool.
     * @param sharesToBurn How many LP shares to redeem
     */
    function removeLiquidity(uint256 sharesToBurn) external nonReentrant returns (uint256 amountA, uint256 amountB) {
        require(sharesToBurn > 0, "Pool: zero shares");
        require(lpShares[msg.sender] >= sharesToBurn, "Pool: insufficient shares");

        // Calculate proportional withdrawal
        amountA = (sharesToBurn * reserveA) / totalLPShares;
        amountB = (sharesToBurn * reserveB) / totalLPShares;

        require(amountA > 0 && amountB > 0, "Pool: zero withdrawal");

        lpShares[msg.sender] -= sharesToBurn;
        totalLPShares -= sharesToBurn;
        reserveA -= amountA;
        reserveB -= amountB;
        totalLiquidityRemoved += amountA + amountB;

        // Transfer tokens out
        tokenA.safeTransfer(msg.sender, amountA);
        tokenB.safeTransfer(msg.sender, amountB);

        emit LiquidityRemoved(
            msg.sender,
            amountA,
            amountB,
            sharesToBurn,
            reserveA,
            reserveB,
            block.timestamp
        );
    }

    /**
     * @notice Swap tokens using constant-product formula (x * y = k).
     * @param tokenIn Address of token being sold
     * @param amountIn Amount of input token
     */
    function swap(address tokenIn, uint256 amountIn) external nonReentrant returns (uint256 amountOut) {
        require(amountIn > 0, "Pool: zero input");
        require(
            tokenIn == address(tokenA) || tokenIn == address(tokenB),
            "Pool: invalid token"
        );
        require(reserveA > 0 && reserveB > 0, "Pool: no liquidity");

        bool isAtoB = (tokenIn == address(tokenA));

        // Calculate fee
        uint256 fee = (amountIn * swapFeeNumerator) / swapFeeDenominator;
        uint256 amountInAfterFee = amountIn - fee;

        // Constant product: (rA + dA) * (rB - dB) = rA * rB
        if (isAtoB) {
            amountOut = (amountInAfterFee * reserveB) / (reserveA + amountInAfterFee);
            require(amountOut > 0 && amountOut < reserveB, "Pool: insufficient out");

            tokenA.safeTransferFrom(msg.sender, address(this), amountIn);
            tokenB.safeTransfer(msg.sender, amountOut);

            reserveA += amountIn; // includes fee
            reserveB -= amountOut;
            totalFeesCollectedA += fee;
        } else {
            amountOut = (amountInAfterFee * reserveA) / (reserveB + amountInAfterFee);
            require(amountOut > 0 && amountOut < reserveA, "Pool: insufficient out");

            tokenB.safeTransferFrom(msg.sender, address(this), amountIn);
            tokenA.safeTransfer(msg.sender, amountOut);

            reserveB += amountIn; // includes fee
            reserveA -= amountOut;
            totalFeesCollectedB += fee;
        }

        totalSwaps++;

        emit Swap(
            msg.sender,
            tokenIn,
            isAtoB ? address(tokenB) : address(tokenA),
            amountIn,
            amountOut,
            fee,
            reserveA,
            reserveB,
            block.timestamp
        );

        // Detect large swaps (> 5% of reserve)
        uint256 reserveIn = isAtoB ? reserveA : reserveB;
        if (amountIn * 100 > reserveIn * 5) { // > 5%
            uint256 priceImpactBps = (amountIn * 10000) / reserveIn;
            emit LargeSwapDetected(
                msg.sender,
                amountIn,
                amountOut,
                priceImpactBps,
                block.timestamp
            );
        }
    }

    /**
     * @notice Owner can inject tokens to rebalance (simulate market maker).
     */
    function rebalance(uint256 addA, uint256 addB, string calldata reason) external onlyOwner {
        uint256 oldA = reserveA;
        uint256 oldB = reserveB;

        if (addA > 0) {
            tokenA.safeTransferFrom(msg.sender, address(this), addA);
            reserveA += addA;
        }
        if (addB > 0) {
            tokenB.safeTransferFrom(msg.sender, address(this), addB);
            reserveB += addB;
        }

        emit PoolRebalanced(
            msg.sender,
            oldA, oldB,
            reserveA, reserveB,
            reason,
            block.timestamp
        );
    }

    // ──────────────────── View functions ─────────────

    function getReserves() external view returns (uint256, uint256) {
        return (reserveA, reserveB);
    }

    function getPoolStats() external view returns (
        uint256 _reserveA,
        uint256 _reserveB,
        uint256 _totalLPShares,
        uint256 _totalSwaps,
        uint256 _feesA,
        uint256 _feesB
    ) {
        return (reserveA, reserveB, totalLPShares, totalSwaps, totalFeesCollectedA, totalFeesCollectedB);
    }

    function getLPBalance(address user) external view returns (uint256) {
        return lpShares[user];
    }

    function getSwapQuote(address tokenIn, uint256 amountIn) external view returns (uint256 amountOut, uint256 fee) {
        require(reserveA > 0 && reserveB > 0, "Pool: no liquidity");
        fee = (amountIn * swapFeeNumerator) / swapFeeDenominator;
        uint256 amountInAfterFee = amountIn - fee;

        bool isAtoB = (tokenIn == address(tokenA));
        if (isAtoB) {
            amountOut = (amountInAfterFee * reserveB) / (reserveA + amountInAfterFee);
        } else {
            amountOut = (amountInAfterFee * reserveA) / (reserveB + amountInAfterFee);
        }
    }

    // ──────────────────── Internal ──────────────────

    function _sqrt(uint256 x) internal pure returns (uint256 y) {
        if (x == 0) return 0;
        uint256 z = (x + 1) / 2;
        y = x;
        while (z < y) {
            y = z;
            z = (x / z + z) / 2;
        }
    }
}

contracts/GenesisToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title GenesisToken
 * @notice Simple ERC20 used as the monitored token in Genesis demos.
 *         Owner can mint tokens for testing transfers, whale movements, etc.
 */
contract GenesisToken is ERC20, Ownable {
    uint8 private _decimals;

    constructor(
        string memory name_,
        string memory symbol_,
        uint8 decimals_,
        uint256 initialSupply
    ) ERC20(name_, symbol_) Ownable(msg.sender) {
        _decimals = decimals_;
        _mint(msg.sender, initialSupply * (10 ** decimals_));
    }

    function decimals() public view override returns (uint8) {
        return _decimals;
    }

    function mint(address to, uint256 amount) external onlyOwner {
        _mint(to, amount);
    }

    function burn(uint256 amount) external {
        _burn(msg.sender, amount);
    }
}

contracts/GenesisVault.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title GenesisVault
 * @notice On-chain vault that emits rich events for every deposit, withdrawal,
 *         and internal transfer.  Genesis monitors these events in real time.
 *
 *         KEY BLOCKCHAIN FEATURES SHOWCASED:
 *           1. Event-driven architecture (Solidity `event`)
 *           2. Inbound/outbound tracking on-chain
 *           3. Reentrancy protection
 *           4. Per-user balance accounting
 *           5. Emergency pause (circuit-breaker pattern)
 */
contract GenesisVault is Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    // ──────────────────── State ────────────────────
    IERC20 public immutable token;

    mapping(address => uint256) public balances;
    uint256 public totalDeposits;
    uint256 public totalWithdrawals;
    bool public paused;

    // ──────────────────── Events (the core of Genesis monitoring) ────────
    /// @notice Emitted on every deposit — Genesis indexes this immediately.
    event Deposit(
        address indexed user,
        uint256 amount,
        uint256 newBalance,
        uint256 timestamp
    );

    /// @notice Emitted on every withdrawal — potential "outbound whale" signal.
    event Withdrawal(
        address indexed user,
        uint256 amount,
        uint256 remainingBalance,
        uint256 timestamp
    );

    /// @notice Vault-internal transfer between two users.
    event InternalTransfer(
        address indexed from,
        address indexed to,
        uint256 amount,
        uint256 timestamp
    );

    /// @notice Large movement detected on-chain — threshold exceeded.
    event LargeMovement(
        address indexed user,
        string movementType,  // "deposit" | "withdrawal"
        uint256 amount,
        uint256 thresholdUsed,
        uint256 timestamp
    );

    /// @notice Emergency pause toggled.
    event EmergencyAction(
        string action,   // "paused" | "unpaused"
        address indexed triggeredBy,
        uint256 timestamp
    );

    // ──────────────────── Modifiers ─────────────────
    modifier whenNotPaused() {
        require(!paused, "Vault: paused");
        _;
    }

    // ──────────────────── Constructor ────────────────
    constructor(address tokenAddress) Ownable(msg.sender) {
        token = IERC20(tokenAddress);
    }

    // ──────────────────── Core functions ─────────────

    /**
     * @notice Deposit tokens into the vault.
     * @param amount Number of token units (with decimals).
     */
    function deposit(uint256 amount) external nonReentrant whenNotPaused {
        require(amount > 0, "Vault: zero amount");

        token.safeTransferFrom(msg.sender, address(this), amount);
        balances[msg.sender] += amount;
        totalDeposits += amount;

        emit Deposit(msg.sender, amount, balances[msg.sender], block.timestamp);

        // On-chain large-movement detection (threshold hard-coded for demo;
        // the ThresholdEngine contract provides user-customisable thresholds).
        if (amount >= 100_000 * 1e6) {
            emit LargeMovement(msg.sender, "deposit", amount, 100_000 * 1e6, block.timestamp);
        }
    }

    /**
     * @notice Withdraw tokens from the vault.
     */
    function withdraw(uint256 amount) external nonReentrant whenNotPaused {
        require(amount > 0, "Vault: zero amount");
        require(balances[msg.sender] >= amount, "Vault: insufficient balance");

        balances[msg.sender] -= amount;
        totalWithdrawals += amount;
        token.safeTransfer(msg.sender, amount);

        emit Withdrawal(msg.sender, amount, balances[msg.sender], block.timestamp);

        if (amount >= 100_000 * 1e6) {
            emit LargeMovement(msg.sender, "withdrawal", amount, 100_000 * 1e6, block.timestamp);
        }
    }

    /**
     * @notice Transfer balance to another vault user without touching the token contract.
     *         Demonstrates internal accounting — useful for Layer-2 patterns.
     */
    function internalTransfer(address to, uint256 amount) external nonReentrant whenNotPaused {
        require(to != address(0) && to != msg.sender, "Vault: invalid recipient");
        require(balances[msg.sender] >= amount, "Vault: insufficient balance");

        balances[msg.sender] -= amount;
        balances[to] += amount;

        emit InternalTransfer(msg.sender, to, amount, block.timestamp);
    }

    // ──────────────────── Admin / Emergency ─────────

    function pause() external onlyOwner {
        paused = true;
        emit EmergencyAction("paused", msg.sender, block.timestamp);
    }

    function unpause() external onlyOwner {
        paused = false;
        emit EmergencyAction("unpaused", msg.sender, block.timestamp);
    }

    // ──────────────────── View helpers ───────────────

    function getVaultStats() external view returns (
        uint256 _totalDeposits,
        uint256 _totalWithdrawals,
        uint256 _vaultBalance,
        bool _paused
    ) {
        return (totalDeposits, totalWithdrawals, token.balanceOf(address(this)), paused);
    }
}

contracts/GenesisVesting.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title GenesisVesting
 * @notice On-chain token vesting with cliff + linear unlock.
 *         Genesis monitors all vesting events in real time.
 *
 *         KEY FEATURES:
 *           1. Create vesting schedules for any beneficiary
 *           2. Cliff period — no tokens unlockable until cliff passes
 *           3. Linear vesting — tokens unlock gradually after cliff
 *           4. Claim — beneficiary claims unlocked tokens
 *           5. Revoke — owner can revoke unvested tokens
 *           6. Rich events for every action (Genesis monitors these)
 */
contract GenesisVesting is Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    // ──────────────────── Types ─────────────────────
    struct VestingSchedule {
        address beneficiary;
        address token;
        uint256 totalAmount;       // total tokens locked
        uint256 claimedAmount;     // tokens already claimed
        uint256 startTime;         // vesting start timestamp
        uint256 cliffDuration;     // seconds until cliff ends
        uint256 vestingDuration;   // total vesting duration (includes cliff)
        bool revoked;
        string description;        // e.g. "Team allocation - 12 month vest"
    }

    // ──────────────────── Storage ───────────────────
    VestingSchedule[] public schedules;
    
    // beneficiary → list of their schedule IDs
    mapping(address => uint256[]) public beneficiarySchedules;

    // ──────────────────── Events ────────────────────
    event VestingCreated(
        uint256 indexed scheduleId,
        address indexed beneficiary,
        address indexed token,
        uint256 totalAmount,
        uint256 cliffDuration,
        uint256 vestingDuration,
        string description,
        uint256 timestamp
    );

    event TokensClaimed(
        uint256 indexed scheduleId,
        address indexed beneficiary,
        uint256 amountClaimed,
        uint256 totalClaimed,
        uint256 remainingLocked,
        uint256 timestamp
    );

    event VestingRevoked(
        uint256 indexed scheduleId,
        address indexed beneficiary,
        uint256 unvestedAmount,
        uint256 vestedUnclaimed,
        uint256 timestamp
    );

    event UnlockMilestone(
        uint256 indexed scheduleId,
        address indexed beneficiary,
        string milestone,   // "cliff_reached" | "25_percent" | "50_percent" | "75_percent" | "fully_vested"
        uint256 unlockedAmount,
        uint256 timestamp
    );

    // ──────────────────── Constructor ────────────────
    constructor() Ownable(msg.sender) {}

    // ──────────────────── Core functions ─────────────

    /**
     * @notice Create a new vesting schedule. Tokens are transferred to this contract.
     * @param beneficiary Address that will receive vested tokens
     * @param token ERC20 token address
     * @param totalAmount Total tokens to vest
     * @param cliffDuration Seconds until cliff (0 = no cliff)
     * @param vestingDuration Total duration in seconds (must be >= cliffDuration)
     * @param description Human-readable description
     */
    function createVesting(
        address beneficiary,
        address token,
        uint256 totalAmount,
        uint256 cliffDuration,
        uint256 vestingDuration,
        string calldata description
    ) external onlyOwner returns (uint256 scheduleId) {
        require(beneficiary != address(0), "Vesting: zero address");
        require(totalAmount > 0, "Vesting: zero amount");
        require(vestingDuration >= cliffDuration, "Vesting: duration < cliff");
        require(vestingDuration > 0, "Vesting: zero duration");

        // Transfer tokens to this contract
        IERC20(token).safeTransferFrom(msg.sender, address(this), totalAmount);

        scheduleId = schedules.length;
        schedules.push(VestingSchedule({
            beneficiary: beneficiary,
            token: token,
            totalAmount: totalAmount,
            claimedAmount: 0,
            startTime: block.timestamp,
            cliffDuration: cliffDuration,
            vestingDuration: vestingDuration,
            revoked: false,
            description: description
        }));

        beneficiarySchedules[beneficiary].push(scheduleId);

        emit VestingCreated(
            scheduleId,
            beneficiary,
            token,
            totalAmount,
            cliffDuration,
            vestingDuration,
            description,
            block.timestamp
        );
    }

    /**
     * @notice Claim unlocked (vested) tokens.
     * @param scheduleId The vesting schedule to claim from
     */
    function claim(uint256 scheduleId) external nonReentrant {
        VestingSchedule storage s = schedules[scheduleId];
        require(msg.sender == s.beneficiary, "Vesting: not beneficiary");
        require(!s.revoked, "Vesting: revoked");

        uint256 vested = _vestedAmount(s);
        uint256 claimable = vested - s.claimedAmount;
        require(claimable > 0, "Vesting: nothing to claim");

        s.claimedAmount += claimable;

        IERC20(s.token).safeTransfer(s.beneficiary, claimable);

        uint256 remaining = s.totalAmount - s.claimedAmount;

        emit TokensClaimed(
            scheduleId,
            s.beneficiary,
            claimable,
            s.claimedAmount,
            remaining,
            block.timestamp
        );

        // Check milestones
        _checkMilestones(scheduleId, s);
    }

    /**
     * @notice Owner revokes unvested tokens (e.g., team member leaves).
     * @param scheduleId The schedule to revoke
     */
    function revoke(uint256 scheduleId) external onlyOwner {
        VestingSchedule storage s = schedules[scheduleId];
        require(!s.revoked, "Vesting: already revoked");

        uint256 vested = _vestedAmount(s);
        uint256 unvested = s.totalAmount - vested;
        uint256 vestedUnclaimed = vested - s.claimedAmount;

        s.revoked = true;

        // Return unvested tokens to owner
        if (unvested > 0) {
            IERC20(s.token).safeTransfer(owner(), unvested);
        }

        emit VestingRevoked(
            scheduleId,
            s.beneficiary,
            unvested,
            vestedUnclaimed,
            block.timestamp
        );
    }

    /**
     * @notice Simulate time passing for demo purposes (owner only).
     *         In production this would not exist — time passes naturally.
     */
    function simulateTimePass(uint256 scheduleId, uint256 secondsToAdvance) external onlyOwner {
        VestingSchedule storage s = schedules[scheduleId];
        s.startTime -= secondsToAdvance;

        // Emit milestone if cliff just passed
        uint256 vested = _vestedAmount(s);
        if (vested > 0 && s.claimedAmount == 0) {
            uint256 pct = (vested * 100) / s.totalAmount;
            string memory milestone;
            if (block.timestamp >= s.startTime + s.cliffDuration && pct >= 1) {
                milestone = "cliff_reached";
            } else if (pct >= 75) {
                milestone = "75_percent";
            } else if (pct >= 50) {
                milestone = "50_percent";
            } else if (pct >= 25) {
                milestone = "25_percent";
            }
            
            if (bytes(milestone).length > 0) {
                emit UnlockMilestone(scheduleId, s.beneficiary, milestone, vested, block.timestamp);
            }
        }
    }

    // ──────────────────── View functions ─────────────

    function getScheduleCount() external view returns (uint256) {
        return schedules.length;
    }

    function getClaimable(uint256 scheduleId) external view returns (uint256) {
        VestingSchedule storage s = schedules[scheduleId];
        if (s.revoked) return 0;
        uint256 vested = _vestedAmount(s);
        return vested - s.claimedAmount;
    }

    function getVestedAmount(uint256 scheduleId) external view returns (uint256) {
        return _vestedAmount(schedules[scheduleId]);
    }

    function getBeneficiaryScheduleCount(address beneficiary) external view returns (uint256) {
        return beneficiarySchedules[beneficiary].length;
    }

    function getScheduleInfo(uint256 scheduleId) external view returns (
        address beneficiary,
        uint256 totalAmount,
        uint256 claimedAmount,
        uint256 claimable,
        uint256 vestingProgress, // 0-100
        bool revoked,
        string memory description
    ) {
        VestingSchedule storage s = schedules[scheduleId];
        uint256 vested = _vestedAmount(s);
        return (
            s.beneficiary,
            s.totalAmount,
            s.claimedAmount,
            s.revoked ? 0 : vested - s.claimedAmount,
            s.totalAmount > 0 ? (vested * 100) / s.totalAmount : 0,
            s.revoked,
            s.description
        );
    }

    // ──────────────────── Internal ──────────────────

    function _vestedAmount(VestingSchedule storage s) internal view returns (uint256) {
        if (block.timestamp < s.startTime + s.cliffDuration) {
            return 0; // Still in cliff period
        }

        uint256 elapsed = block.timestamp - s.startTime;
        if (elapsed >= s.vestingDuration) {
            return s.totalAmount; // Fully vested
        }

        // Linear vesting after cliff
        return (s.totalAmount * elapsed) / s.vestingDuration;
    }

    function _checkMilestones(uint256 scheduleId, VestingSchedule storage s) internal {
        uint256 pct = (s.claimedAmount * 100) / s.totalAmount;
        
        if (s.claimedAmount == s.totalAmount) {
            emit UnlockMilestone(scheduleId, s.beneficiary, "fully_vested", s.totalAmount, block.timestamp);
        } else if (pct >= 75) {
            emit UnlockMilestone(scheduleId, s.beneficiary, "75_percent", s.claimedAmount, block.timestamp);
        } else if (pct >= 50) {
            emit UnlockMilestone(scheduleId, s.beneficiary, "50_percent", s.claimedAmount, block.timestamp);
        } else if (pct >= 25) {
            emit UnlockMilestone(scheduleId, s.beneficiary, "25_percent", s.claimedAmount, block.timestamp);
        }
    }
}

contracts/ThresholdEngine.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title ThresholdEngine
 * @notice **User-customisable** on-chain threshold rules.
 *         Any user can register their own alert thresholds for tokens/vaults.
 *         Genesis reads these thresholds to decide when to fire alerts.
 *
 *         KEY BLOCKCHAIN FEATURES:
 *           1. Fully on-chain configuration — no off-chain database needed
 *           2. Each user controls their own thresholds
 *           3. Events emitted for every config change (auditable)
 *           4. Enumerable: Genesis can query all active thresholds
 */
contract ThresholdEngine is Ownable {

    // ──────────────────── Types ─────────────────────
    enum AlertType { LARGE_TRANSFER, WHALE_MOVEMENT, RAPID_FLOW, CUSTOM }

    struct ThresholdRule {
        address token;           // which token to monitor (address(0) = any)
        AlertType alertType;
        uint256 threshold;       // minimum amount to trigger
        uint256 cooldownSec;     // minimum seconds between alerts
        bool enabled;
        string description;      // human-readable (stored on-chain)
    }

    // ──────────────────── Storage ───────────────────
    // user → ruleIndex → ThresholdRule
    mapping(address => ThresholdRule[]) public userRules;
    
    // Global default thresholds (set by contract owner / protocol)
    ThresholdRule[] public globalRules;

    // All users who have ever set a rule (for enumeration)
    address[] public activeUsers;
    mapping(address => bool) private _isActiveUser;

    // ──────────────────── Events ────────────────────
    event ThresholdSet(
        address indexed user,
        uint256 indexed ruleIndex,
        address token,
        AlertType alertType,
        uint256 threshold,
        uint256 cooldownSec,
        string description,
        uint256 timestamp
    );

    event ThresholdUpdated(
        address indexed user,
        uint256 indexed ruleIndex,
        uint256 oldThreshold,
        uint256 newThreshold,
        uint256 timestamp
    );

    event ThresholdRemoved(
        address indexed user,
        uint256 indexed ruleIndex,
        uint256 timestamp
    );

    event GlobalThresholdSet(
        uint256 indexed ruleIndex,
        address token,
        AlertType alertType,
        uint256 threshold,
        string description,
        uint256 timestamp
    );

    // ──────────────────── Constructor ────────────────
    constructor() Ownable(msg.sender) {}

    // ──────────────────── User functions ─────────────

    /**
     * @notice Create a new alert threshold. Fully user-controlled.
     */
    function setThreshold(
        address token,
        AlertType alertType,
        uint256 threshold,
        uint256 cooldownSec,
        string calldata description
    ) external returns (uint256 ruleIndex) {
        require(threshold > 0, "Threshold must be > 0");

        ThresholdRule memory rule = ThresholdRule({
            token: token,
            alertType: alertType,
            threshold: threshold,
            cooldownSec: cooldownSec,
            enabled: true,
            description: description
        });

        userRules[msg.sender].push(rule);
        ruleIndex = userRules[msg.sender].length - 1;

        // Track active user
        if (!_isActiveUser[msg.sender]) {
            _isActiveUser[msg.sender] = true;
            activeUsers.push(msg.sender);
        }

        emit ThresholdSet(
            msg.sender,
            ruleIndex,
            token,
            alertType,
            threshold,
            cooldownSec,
            description,
            block.timestamp
        );
    }

    /**
     * @notice Update an existing threshold value.
     */
    function updateThreshold(uint256 ruleIndex, uint256 newThreshold) external {
        require(ruleIndex < userRules[msg.sender].length, "Invalid rule index");
        require(newThreshold > 0, "Threshold must be > 0");

        uint256 old = userRules[msg.sender][ruleIndex].threshold;
        userRules[msg.sender][ruleIndex].threshold = newThreshold;

        emit ThresholdUpdated(msg.sender, ruleIndex, old, newThreshold, block.timestamp);
    }

    /**
     * @notice Disable a threshold (soft delete).
     */
    function removeThreshold(uint256 ruleIndex) external {
        require(ruleIndex < userRules[msg.sender].length, "Invalid rule index");
        userRules[msg.sender][ruleIndex].enabled = false;

        emit ThresholdRemoved(msg.sender, ruleIndex, block.timestamp);
    }

    // ──────────────────── Owner / global rules ──────

    /**
     * @notice Owner sets protocol-wide default thresholds.
     */
    function setGlobalThreshold(
        address token,
        AlertType alertType,
        uint256 threshold,
        uint256 cooldownSec,
        string calldata description
    ) external onlyOwner returns (uint256 ruleIndex) {
        globalRules.push(ThresholdRule({
            token: token,
            alertType: alertType,
            threshold: threshold,
            cooldownSec: cooldownSec,
            enabled: true,
            description: description
        }));
        ruleIndex = globalRules.length - 1;

        emit GlobalThresholdSet(ruleIndex, token, alertType, threshold, description, block.timestamp);
    }

    // ──────────────────── View helpers ───────────────

    function getUserRuleCount(address user) external view returns (uint256) {
        return userRules[user].length;
    }

    function getUserRule(address user, uint256 index) external view returns (ThresholdRule memory) {
        return userRules[user][index];
    }

    function getGlobalRuleCount() external view returns (uint256) {
        return globalRules.length;
    }

    function getActiveUserCount() external view returns (uint256) {
        return activeUsers.length;
    }

    /**
     * @notice Returns all enabled thresholds for a given user + global rules.
     *         Genesis calls this to build the active rule set.
     */
    function getActiveThresholds(address user) external view returns (ThresholdRule[] memory) {
        uint256 userCount = userRules[user].length;
        uint256 globalCount = globalRules.length;
        
        // Count enabled rules
        uint256 enabledCount = 0;
        for (uint256 i = 0; i < userCount; i++) {
            if (userRules[user][i].enabled) enabledCount++;
        }
        for (uint256 i = 0; i < globalCount; i++) {
            if (globalRules[i].enabled) enabledCount++;
        }

        ThresholdRule[] memory result = new ThresholdRule[](enabledCount);
        uint256 idx = 0;

        for (uint256 i = 0; i < userCount; i++) {
            if (userRules[user][i].enabled) {
                result[idx++] = userRules[user][i];
            }
        }
        for (uint256 i = 0; i < globalCount; i++) {
            if (globalRules[i].enabled) {
                result[idx++] = globalRules[i];
            }
        }

        return result;
    }
}

